changeSet.sql:

DROP SCHEMA public CASCADE;
CREATE SCHEMA public;
GRANT ALL ON SCHEMA public TO postgres;
GRANT ALL ON SCHEMA public TO public;

CREATE SEQUENCE company_seq; CREATE TABLE "company" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('company_seq'), "name" VARCHAR(100), "address" VARCHAR(200), "website" VARCHAR(100), "email" VARCHAR(100), "phone_number" VARCHAR(50), "description" VARCHAR(300), "type" VARCHAR(30) );
CREATE SEQUENCE customer_seq; CREATE TABLE "customer" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('customer_seq'), "first_name" VARCHAR(100), "last_name" VARCHAR(200), "email" VARCHAR(100), "phone_number" VARCHAR(50) );
CREATE SEQUENCE employee_seq; CREATE TABLE "employee" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('employee_seq'), "username" VARCHAR(100) NOT NULL UNIQUE, "password" VARCHAR(100), "email" VARCHAR(100), "first_name" VARCHAR(100), "last_name" VARCHAR(200), "phone_number" VARCHAR(50), "company" BIGINT );
CREATE SEQUENCE landmark_seq; CREATE TABLE "landmark" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('landmark_seq'), "name" VARCHAR(100), "location" VARCHAR(200), "description" VARCHAR(300) );
CREATE SEQUENCE trip_seq; CREATE TABLE "trip" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('trip_seq'), "landmark" BIGINT, "company" BIGINT, "date" DATE, "start_time" TIME WITHOUT TIME ZONE, "price" DOUBLE PRECISION, "available_places" INTEGER );
CREATE SEQUENCE booking_seq; CREATE TABLE "booking" ( "id" BIGINT NOT NULL PRIMARY KEY DEFAULT nextval('booking_seq'), "trip" BIGINT, "customer" VARCHAR(100), "phone_number" VARCHAR(50), "tickets" INT );
